import React, { Component } from 'react';
import Tabs from 'components/App/Tabs';

const ReactCSSTransitionGroup = React.addons.CSSTransitionGroup;

const ActiveTabContent = (props) => <div>{props.content}</div>;

const tabList = [
  {
    name: "Pictures",
    icon: "",
    content: "Stuff 1"
  }, {
    name: "Music",
    icon: "",
    content: "Stuff 2"
  }, {
    name: "Videos",
    icon: "",
    content: "Stuff 3"
  }, {
    name: "Documents",
    icon: "",
    content: "Stuff 4"
  }
];

class WeatherTabs extends Component {
  constructor(props) {
    super(props);

    this.state = {
      activeTab: 'Pictures'
    };
  }

  changeActiveTab(tab) {
    this.setState({ activeTab: tab });
  }

  activeTabContent() {
    const activeIndex = tabList.findIndex((tab) => {
      return tab.name === this.state.activeTab;
    });

    return tabList[activeIndex].content;
  }

  render() {
    return (
      <div className="container">
        <Tabs tabList={tabList}
              activeTab={this.state.activeTab}
              changeActiveTab={this.changeActiveTab.bind(this)}
        />

        <ReactCSSTransitionGroup
          className="tabs-content"
          component="div"
          transitionName="fade"
          transitionEnterTimeout={0}
          transitionLeaveTimeout={150}
        >
          <ActiveTabContent key={this.state.activeTab} content={this.activeTabContent()} />
        </ReactCSSTransitionGroup>

    </div>
    );
  }
}

export default WeatherTabs;
