export function fetchWeather() {
  const city = 'Warsaw';
  const url = `${ROOT_URL}&q=${city},pl`;
  return function(dispatch) {
    return axios.get(url)
      .then(({ data }) => {
      dispatch(fetchWeather(data));
    });
  };
}
