import axios from 'axios';
import { FETCH_WEATHER } from 'redux/actions/types';

const API_KEY = '1d1ed4c5c279b3103998e234955244b6';
const ROOT_URL = `https://api.openweathermap.org/data/2.5/forecast?appid=${API_KEY}`;

const sample_url = 'https://api.openweathermap.org/data/2.5/forecast?appid=02230cf0e31741f5ca1a2b8f7d73c966&q=lublin,pl';

export function fetchWeather() {
  const response = axios.get(sample_url)
    .then(res => res)
    .catch(err => { console.log(err) });
  return {
    type: FETCH_WEATHER,
    payload: response
  };
}
